pragma solidity ^0.8.28;

import { IERC20 } from "@openzeppelin/contracts/interfaces/IERC20.sol";
import { ERC1155HolderUpgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/interfaces/IERC20Metadata.sol";
import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import { TransparentUpgradeableProxy } from "@openzeppelin/contracts/proxy/transparent/TransparentUpgradeableProxy.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import { TokenPayment, TokenPayments } from "./libraries/TokenPayments.sol";
import { OracleLibrary } from "./libraries/OracleLibrary.sol";
import { FixedPoint128 } from "./libraries/FixedPoint128.sol";
import { FullMath } from "./libraries/FullMath.sol";
import { Number } from "./libraries/Number.sol";
import { Epochs } from "./libraries/Epochs.sol";
import "./libraries/utils.sol";
import { DeployGToken } from "./libraries/DeployGToken.sol";
import { DeployLaunchPair } from "./libraries/DeployLaunchPair.sol";

import { GToken, GTokenLib, GTokenBalance } from "./tokens/GToken/GToken.sol";
import { WNTV } from "./tokens/WNTV.sol";
import { Gainz } from "./tokens/Gainz/Gainz.sol";

import { Pair } from "./Pair.sol";
import { Router } from "./Router.sol";
import { PriceOracle } from "./PriceOracle.sol";
import { LaunchPair } from "./LaunchPair.sol";

import "./types.sol";
import "./errors.sol";

uint256 constant MIN_LIQ_VALUE_FOR_LISTING = 3_000e18;

library GovernanceLib {
	using Epochs for Epochs.Storage;
	using GTokenLib for GTokenLib.Attributes;
	using TokenPayments for TokenPayment;
	using TokenPayments for address;
	using Number for uint256;
	using EnumerableSet for EnumerableSet.AddressSet;

	/// @notice Validates the GToken payment for the listing based on the total ADEX amount in liquidity.
	/// @param payment The payment details for the GToken.
	/// @return bool indicating if the GToken payment is valid.
	function _isValidGTokenPaymentForListing(
		TokenPayment calldata payment,
		address gtoken,
		address gainzToken,
		uint256 currentEpoch
	) private view returns (bool) {
		// Ensure the payment token is the correct GToken contract
		if (payment.token != gtoken) {
			return false;
		}

		// Retrieve the GToken attributes for the specified nonce
		GTokenLib.Attributes memory attributes = GToken(gtoken)
			.getBalanceAt(msg.sender, payment.nonce)
			.attributes;

		require(
			attributes.epochsLeft(currentEpoch) >= 1000,
			"Security GToken Payment Expired"
		);

		return
			(attributes.lpDetails.token0 == gainzToken ||
				attributes.lpDetails.token1 == gainzToken) &&
			payment.amount >= MIN_LIQ_VALUE_FOR_LISTING;
	}

	function _calculateClaimableReward(
		address user,
		uint256 nonce,
		address gtoken,
		uint256 rewardPerShare
	)
		internal
		view
		returns (
			uint256 claimableReward,
			GTokenLib.Attributes memory attributes
		)
	{
		attributes = GToken(gtoken).getBalanceAt(user, nonce).attributes;

		claimableReward = FullMath.mulDiv(
			attributes.stakeWeight,
			rewardPerShare - attributes.rewardPerShare,
			FixedPoint128.Q128
		);
	}

	function unStake(
		Governance.GovernanceStorage storage $,
		uint256 nonce,
		uint amount0Min,
		uint amount1Min
	) external {
		Gainz($.gainzToken).mintGainz();

		address user = msg.sender;

		// Calculate rewards to be claimed on unstaking
		(
			uint256 claimableReward,
			GTokenLib.Attributes memory attributes
		) = _calculateClaimableReward(user, nonce, $.gtoken, $.rewardPerShare);

		// Transfer the claimable rewards to the user, if any
		if (claimableReward > 0) {
			$.rewardsReserve -= claimableReward;
			IERC20($.gainzToken).transfer(user, claimableReward);
		}

		// Calculate the amount of GToken tokens to return to the user
		uint256 liquidity = attributes.lpDetails.liquidity;
		uint256 liquidityToReturn = attributes.epochsLocked == 0
			? liquidity
			: attributes.valueToKeep(liquidity, $.epochs.currentEpoch());

		Pair pair = Pair(
			PriceOracle(OracleLibrary.oracleAddress($.router)).pairFor(
				attributes.lpDetails.token0,
				attributes.lpDetails.token1
			)
		);

		if (liquidityToReturn < liquidity) {
			address feeTo = Router(payable($.router)).feeTo();
			require(
				feeTo != address(0) && feeTo != address(this),
				"Governance: INVALID_FEE_TO_ADDRESS"
			);

			pair.transfer(feeTo, liquidity - liquidityToReturn);

			// Adjust slippage accordingly
			amount0Min = (amount0Min * liquidityToReturn) / liquidity;
			amount1Min = (amount1Min * liquidityToReturn) / liquidity;
		}

		// Transfer GToken tokens back to the user
		pair.approve($.router, liquidityToReturn);
		(uint256 amount0, uint256 amount1) = Router(payable($.router))
			.removeLiquidity(
				attributes.lpDetails.token0,
				attributes.lpDetails.token1,
				liquidityToReturn,
				amount0Min,
				amount1Min,
				address(this),
				block.timestamp + 1
			);

		// Set these values to 0 and updating the atttributes at nonce effectively burns the token
		attributes.lpDetails.liquidity = 0;
		attributes.lpDetails.liqValue = 0;
		nonce = GToken($.gtoken).update(user, nonce, attributes);

		attributes.lpDetails.token0.sendFungibleToken(amount0, user);
		attributes.lpDetails.token1.sendFungibleToken(amount1, user);
	}

	function proposeNewPairListing(
		Governance.GovernanceStorage storage $,
		TokenPayment calldata securityPayment,
		TokenPayment calldata tradeTokenPayment
	) external {
		address tradeToken = tradeTokenPayment.token;

		// Ensure there is no active listing proposal
		require(
			$.pairOwnerListing[msg.sender].owner == address(0),
			"Governance: Previous proposal not completed"
		);

		// Validate the trade token and ensure it is not already listed
		bool isNewAddition = $.pendingOrListedTokens.add(tradeToken);
		require(
			isERC20(tradeToken) &&
				isNewAddition &&
				!isERC20(
					PriceOracle(OracleLibrary.oracleAddress($.router)).pairFor(
						tradeToken,
						$.wNativeToken
					)
				),
			"Governance: Invalid Trade token"
		);

		if (tradeToken != $.gainzToken) {
			require(
				_isValidGTokenPaymentForListing(
					securityPayment,
					$.gtoken,
					$.gainzToken,
					$.epochs.currentEpoch()
				),
				"Governance: Invalid GToken Payment for proposal"
			);
			securityPayment.receiveTokenFor(
				msg.sender,
				address(this),
				$.wNativeToken
			);
		}

		require(
			tradeTokenPayment.amount > 0,
			"Governance: Must send potential initial liquidity"
		);
		tradeTokenPayment.receiveTokenFor(
			msg.sender,
			address(this),
			$.wNativeToken
		);

		// Update the active listing with the new proposal details
		Governance.TokenListing memory activeListing;
		activeListing.owner = msg.sender;
		activeListing.tradeTokenPayment = tradeTokenPayment;
		activeListing.securityGTokenPayment = securityPayment;
		activeListing.endEpoch = $.epochs.currentEpoch(); // Voting is disabled
		activeListing.campaignId = $.launchPair.createCampaign(msg.sender);

		$.pairOwnerListing[activeListing.owner] = activeListing;
		$.pairOwnerListing[
			activeListing.tradeTokenPayment.token
		] = activeListing;
	}
}

/// @title Governance Contract
/// @notice This contract handles the governance process by allowing users to lock GToken tokens and mint GTokens.
/// @dev This contract interacts with the GTokens library and manages GToken token payments.
contract Governance is ERC1155HolderUpgradeable, OwnableUpgradeable, Errors {
	using Epochs for Epochs.Storage;
	using GTokenLib for GTokenLib.Attributes;
	using TokenPayments for TokenPayment;
	using TokenPayments for address;
	using EnumerableSet for EnumerableSet.AddressSet;
	using EnumerableSet for EnumerableSet.UintSet;

	struct TokenListing {
		uint256 yesVote; // Number of yes votes
		uint256 noVote; // Number of no votes
		uint256 totalGTokenAmount; // Total GToken amount locked for the listing
		uint256 endEpoch; // Epoch when the listing proposal ends
		address owner; // The owner proposing the listing
		TokenPayment securityGTokenPayment;
		TokenPayment tradeTokenPayment; // The token proposed for trading
		uint256 campaignId; // launchPair campaign ID
	}

	/// @custom:storage-location erc7201:gainz.Governance.storage
	struct GovernanceStorage {
		uint256 rewardPerShare;
		uint256 rewardsReserve;
		// The following values should be immutable
		address gtoken;
		address gainzToken;
		address router;
		address wNativeToken;
		Epochs.Storage epochs;
		// New vars after adding launchpair
		uint256 protocolFees;
		address protocolFeesCollector;
		mapping(address => EnumerableSet.UintSet) userVotes;
		mapping(address => address) userVote;
		TokenListing activeListing;
		EnumerableSet.AddressSet pendingOrListedTokens;
		mapping(address => TokenListing) pairOwnerListing;
		LaunchPair launchPair;
	}

	// keccak256(abi.encode(uint256(keccak256("gainz.Governance.storage")) - 1)) & ~bytes32(uint256(0xff));
	bytes32 private constant GOVERNANCE_STORAGE_LOCATION =
		0x8a4dda5430cdcd8aca8f2a075bbbae5f31557dc6b6b93555c9c43f674de00c00;

	function _getGovernanceStorage()
		private
		pure
		returns (GovernanceStorage storage $)
	{
		assembly {
			$.slot := GOVERNANCE_STORAGE_LOCATION
		}
	}

	/// @notice Function to initialize the Governance contract.
	/// @param _epochs The epochs storage instance for managing epochs.
	function initialize(
		Epochs.Storage memory _epochs,
		address gainzToken,
		address wNativeToken,
		address proxyAdmin
	) public initializer {
		address router = msg.sender;
		__Ownable_init(router);

		GovernanceStorage storage $ = _getGovernanceStorage();

		$.epochs = _epochs;
		$.gtoken = DeployGToken.create($.epochs, address(this), proxyAdmin);

		$.router = router;
		$.wNativeToken = wNativeToken;
		require(
			$.wNativeToken != address(0),
			"Governance: INVALID_WRAPPED_NATIVE_TOKEN"
		);

		require(gainzToken != address(0), "Invalid gainzToken");
		$.gainzToken = gainzToken;

		$.launchPair = DeployLaunchPair.newLaunchPair($.gtoken, proxyAdmin);
	}

	error InvalidPayment(TokenPayment payment, uint256 value);

	function _getDesiredToken(
		address[] calldata path,
		TokenPayment calldata stakingPayment,
		uint256 amountOutMin
	) internal returns (TokenPayment memory payment) {
		if (path.length == 0) revert InvalidPath(path);

		uint256 amountIn = stakingPayment.amount / 2;

		payment.token = path[path.length - 1];
		payment.amount = payment.token == stakingPayment.token
			? amountIn
			: Router(payable(_getGovernanceStorage().router))
				.swapExactTokensForTokens(
					amountIn,
					amountOutMin,
					path,
					address(this),
					block.timestamp + 1
				)[path.length - 1][0];
	}

	function _receiveAndApprovePayment(
		TokenPayment memory payment,
		address router
	) internal returns (address wNativeToken) {
		wNativeToken = Router(payable(router)).getWrappedNativeToken();
		bool paymentIsNative = payment.token == wNativeToken;

		if (paymentIsNative) payment.token = address(0);
		payment.receiveTokenFor(msg.sender, address(this), wNativeToken);
		if (paymentIsNative) payment.token = wNativeToken;

		// Optimistically approve `router` to spend payment in `_getDesiredToken` call
		payment.approve(router);
	}

	function _computeLiqValue(
		GovernanceStorage storage $,
		TokenPayment memory paymentA,
		TokenPayment memory paymentB,
		address[] calldata pathToNative
	) internal returns (uint256 value) {
		// Early return if either payment{A,B} is native
		if (paymentA.token == $.wNativeToken) return paymentA.amount;
		if (paymentB.token == $.wNativeToken) return paymentB.amount;

		// Validate the pathToNative
		if (
			pathToNative.length < 2 || // `pathToNative` must have valid length
			pathToNative[pathToNative.length - 1] != $.wNativeToken || // The last token must be the native token (i.e., the reference token)
			(pathToNative[0] != paymentA.token &&
				pathToNative[0] != paymentB.token) // The first token must be one of the payment tokens
		) revert InvalidPath(pathToNative);

		// Determine which payment token to use for the conversion
		TokenPayment memory payment = pathToNative[0] == paymentA.token
			? paymentA
			: paymentB;

		// Get the PriceOracle instance
		PriceOracle priceOracle = PriceOracle(
			OracleLibrary.oracleAddress($.router)
		);

		// Start with the payment amount
		value = payment.amount;

		// Convert the payment amount to the native token using the provided path
		for (uint256 i = 0; i < pathToNative.length - 1; i++) {
			value = priceOracle.updateAndConsult(
				pathToNative[i],
				pathToNative[i + 1],
				value
			);
		}

		// Ensure the computed value is valid
		require(value > 0, "Governance: INVALID_COMPUTED_LIQ_VALUE");
	}

	function stake(
		TokenPayment calldata payment,
		uint256 epochsLocked,
		address[][3] calldata paths, // 0 -> pathA, 1 -> pathB, 2 -> pathToNative
		uint256 amountOutMinA,
		uint256 amountOutMinB
	) external payable returns (uint256) {
		// Validate the payment amount
		if (
			payment.amount == 0 ||
			(msg.value > 0 && payment.amount != msg.value)
		) revert InvalidPayment(payment, msg.value);

		// Retrieve the governance storage
		GovernanceStorage storage $ = _getGovernanceStorage();

		// Initialize liquidity info
		LiquidityInfo memory liqInfo;
		{
			// Receive and approve the payment
			_receiveAndApprovePayment(payment, $.router);

			// Swap the payment tokens into the desired tokens
			TokenPayment memory paymentA = _getDesiredToken(
				paths[0],
				payment,
				amountOutMinA
			);
			TokenPayment memory paymentB = _getDesiredToken(
				paths[1],
				payment,
				amountOutMinB
			);
			require(
				paymentA.token != paymentB.token,
				"Governance: INVALID_PATH_VALUES"
			);

			// Approve the router to spend the swapped tokens
			if (paymentA.token != payment.token) paymentA.approve($.router);
			if (paymentB.token != payment.token) paymentB.approve($.router);

			// Set liquidity info
			(liqInfo.token0, liqInfo.token1) = paymentA.token < paymentB.token
				? (paymentA.token, paymentB.token)
				: (paymentB.token, paymentA.token);

			// Add liquidity using the router
			(, , liqInfo.liquidity, liqInfo.pair) = Router(payable($.router))
				.addLiquidity(paymentA, paymentB, 0, 0, block.timestamp + 1);

			// Compute the liquidity value
			liqInfo.liqValue = payment.token == $.wNativeToken
				? msg.value / 2
				: _computeLiqValue($, paymentA, paymentB, paths[2]);
		}

		// Mint GToken tokens for the user
		return
			GToken($.gtoken).mintGToken(
				msg.sender,
				$.rewardPerShare,
				epochsLocked,
				liqInfo
			);
	}

	function _addGainzMint(
		uint amount,
		uint256 totalStakeWeight
	) private pure returns (uint _rewardsReserve, uint _rewardPerShare) {
		if (totalStakeWeight > 0) {
			// Update the rewards reserve
			_rewardsReserve = amount;
			_rewardPerShare = FullMath.mulDiv(
				amount,
				FixedPoint128.Q128,
				totalStakeWeight
			);
		}
	}

	/// @notice Updates the rewards reserve by adding the specified amount.
	function updateRewardReserve() external {
		GovernanceStorage storage $ = _getGovernanceStorage();

		// Transfer the amount of Gainz tokens to the contract
		uint256 amount = IERC20($.gainzToken).balanceOf(address(this)) -
			$.rewardsReserve;
		uint _rewardPerShare;

		uint256 totalStakeWeight = GToken($.gtoken).totalStakeWeight();
		(amount, _rewardPerShare) = _addGainzMint(amount, totalStakeWeight);
		// Update the rewards reserve
		$.rewardsReserve += amount;
		$.rewardPerShare += _rewardPerShare;
	}

	/// @notice Allows a user to claim their accumulated rewards based on their current stake.
	/// @dev This function will transfer the calculated claimable reward to the user,
	/// 	 update the user's reward attributes, and decrease the rewards reserve.
	/// @param nonce The specific nonce representing a unique staking position of the user.
	/// @return Nonce of the updated GToken for the user after claiming the reward.
	function claimRewards(uint256 nonce) external returns (uint256) {
		GovernanceStorage storage $ = _getGovernanceStorage();

		Gainz($.gainzToken).mintGainz();

		address user = msg.sender;
		(
			uint256 claimableReward,
			GTokenLib.Attributes memory attributes
		) = GovernanceLib._calculateClaimableReward(
				user,
				nonce,
				$.gtoken,
				$.rewardPerShare
			);

		require(claimableReward > 0, "Governance: No rewards to claim");

		$.rewardsReserve -= claimableReward;
		attributes.rewardPerShare = $.rewardPerShare;
		attributes.lastClaimEpoch = $.epochs.currentEpoch();

		IERC20($.gainzToken).transfer(user, claimableReward);
		return GToken($.gtoken).update(user, nonce, attributes);
	}

	function unStake(uint256 nonce, uint amount0Min, uint amount1Min) external {
		GovernanceLib.unStake(
			_getGovernanceStorage(),
			nonce,
			amount0Min,
			amount1Min
		);
	}

	function _returnListingDeposits(TokenListing memory listing) internal {
		GovernanceStorage storage $ = _getGovernanceStorage();

		if (listing.securityGTokenPayment.nonce != 0)
			listing.securityGTokenPayment.sendToken(listing.owner);

		if (listing.tradeTokenPayment.amount > 0) {
			listing.tradeTokenPayment.sendToken(listing.owner);
		}

		delete $.pairOwnerListing[msg.sender];
		delete $.pairOwnerListing[listing.tradeTokenPayment.token];
		$.pendingOrListedTokens.remove(listing.tradeTokenPayment.token);
	}

	/**
	 * @notice Progresses the new pair listing process for the calling address.
	 *         This function handles the various stages of the listing, including
	 *         voting, launch pad campaign, and liquidity provision.
	 */
	function progressNewPairListing() external {
		GovernanceStorage storage $ = _getGovernanceStorage();

		// Retrieve the token listing associated with the caller's address.
		TokenListing storage listing = $.pairOwnerListing[msg.sender];

		// Ensure that a valid listing exists after the potential refresh.
		require(
			listing.owner != address(0) && listing.campaignId > 0,
			"No listing found"
		);

		// Retrieve details of the existing campaign.
		LaunchPair.Campaign memory campaign = $.launchPair.getCampaignDetails(
			listing.campaignId
		);

		if (campaign.goal > 0 && block.timestamp > campaign.deadline) {
			if (campaign.fundsRaised < campaign.goal) {
				campaign.status = LaunchPair.CampaignStatus.Failed;
			} else {
				campaign.status = LaunchPair.CampaignStatus.Success;
			}
		}

		// Check the campaign status.
		if (campaign.status != LaunchPair.CampaignStatus.Success) {
			// If the campaign failed, return the deposits to the listing owner.
			if (campaign.status == LaunchPair.CampaignStatus.Failed) {
				_returnListingDeposits(listing);
				return;
			}

			// If the campaign is not complete, revert the transaction.
			revert("Governance: Funding not complete");
		}

		require(!campaign.isWithdrawn, "Governance: CAMPAIGN_FUNDS_WITHDRAWN");

		// Store the current balance of the contract before withdrawing funds.
		uint256 ethBal = address(this).balance;
		// Withdraw the funds raised in the campaign.
		uint256 fundsRaised = $.launchPair.withdrawFunds(listing.campaignId);
		// Ensure that the funds were successfully withdrawn.
		require(
			ethBal + fundsRaised == address(this).balance,
			"Governance: Funds not withdrawn for campaign"
		);

		listing.tradeTokenPayment.approve($.router);

		// Create the trading pair using the router and receive GToken tokens.
		delete $.pairOwnerListing[listing.tradeTokenPayment.token];
		(address pair, uint256 liquidity) = Router(payable($.router))
			.createPair{ value: fundsRaised }(
			listing.tradeTokenPayment,
			TokenPayment({
				token: $.wNativeToken,
				nonce: 0,
				amount: fundsRaised
			})
		);

		uint liqValue = fundsRaised / 2;

		uint256 gTokenNonce = GToken($.gtoken).mintGToken(
			address(this),
			$.rewardPerShare,
			GTokenLib.MAX_EPOCHS_LOCK,
			LiquidityInfo({
				pair: pair,
				liquidity: liquidity,
				liqValue: liqValue,
				token0: Pair(pair).token0(),
				token1: Pair(pair).token1()
			})
		);

		// Return the security GToken payment after successful governance entry.
		if (listing.securityGTokenPayment.nonce != 0)
			listing.securityGTokenPayment.sendToken(listing.owner);

		TokenPayment memory gTokenPayment = TokenPayment({
			amount: GToken($.gtoken).balanceOf(address(this), gTokenNonce),
			nonce: gTokenNonce,
			token: $.gtoken
		});

		// Approve the GToken tokens for use by the launch pair contract.
		gTokenPayment.approve(address($.launchPair));
		// Transfer the GToken tokens to the launch pair contract.
		$.launchPair.receiveGToken(gTokenPayment, listing.campaignId);
		// complete the proposal
		delete $.pairOwnerListing[msg.sender];
	}

	/// @notice Proposes a new pair listing by submitting the required listing fee and GToken payment.
	/// @param securityPayment The GToken payment as security deposit
	/// @param tradeTokenPayment The the trade token to be listed with launchPair distribution amount, if any.
	function proposeNewPairListing(
		TokenPayment calldata securityPayment,
		TokenPayment calldata tradeTokenPayment
	) external {
		GovernanceLib.proposeNewPairListing(
			_getGovernanceStorage(),
			securityPayment,
			tradeTokenPayment
		);
	}

	// ******* VIEWS *******

	function getGToken() external view returns (address) {
		return _getGovernanceStorage().gtoken;
	}

	function rewardsReserve() external view returns (uint256) {
		return _getGovernanceStorage().rewardsReserve;
	}

	function rewardPerShare() external view returns (uint256) {
		return _getGovernanceStorage().rewardPerShare;
	}

	function getClaimableRewards(
		address user,
		uint256 nonce
	) external view returns (uint256 totalClaimable) {
		GovernanceStorage storage $ = _getGovernanceStorage();

		(, uint256 rpsToAdd) = _addGainzMint(
			Gainz($.gainzToken).stakersGainzToEmit(),
			GToken($.gtoken).totalStakeWeight()
		);

		(totalClaimable, ) = GovernanceLib._calculateClaimableReward(
			user,
			nonce,
			$.gtoken,
			$.rewardPerShare + rpsToAdd
		);
	}

	function launchPair() public view returns (LaunchPair) {
		return _getGovernanceStorage().launchPair;
	}

	function pairListing(
		address pairOwner
	) public view returns (Governance.TokenListing memory) {
		return _getGovernanceStorage().pairOwnerListing[pairOwner];
	}

	function epochs() public view returns (Epochs.Storage memory) {
		return _getGovernanceStorage().epochs;
	}

	function minLiqValueForListing() public pure returns (uint256) {
		return MIN_LIQ_VALUE_FOR_LISTING;
	}

	function currentEpoch() public view returns (uint256) {
		GovernanceStorage storage $ = _getGovernanceStorage();
		return $.epochs.currentEpoch();
	}

	receive() external payable {}
}
","keccak256":"0x7559db4a7b94c25144aae355132ac1890baa53c6d7512ea0ffa255fa0b334ee8","license":"MIT"},"contracts/LaunchPair.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import { ERC1155HolderUpgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";

import { TokenPayment, TokenPayments } from "./libraries/TokenPayments.sol";
import { GToken, GTokenBalance } from "./tokens/GToken/GToken.sol";
import { FullMath } from "./libraries/FullMath.sol";

import "hardhat/console.sol";

/**
 * @title LaunchPair
 * @dev This contract facilitates the creation and management of crowdfunding campaigns for launching new tokens. Participants contribute funds to campaigns, and if the campaign is successful, they receive launchPair tokens in return. If the campaign fails, their contributions are refunded.
 */
contract LaunchPair is OwnableUpgradeable, ERC1155HolderUpgradeable {
	using TokenPayments for TokenPayment;
	using EnumerableSet for EnumerableSet.UintSet;

	enum CampaignStatus {
		Pending,
		Funding,
		Failed,
		Success
	}

	struct Campaign {
		address creator;
		uint256 gtokenNonce;
		uint256 goal;
		uint256 deadline;
		uint256 fundsRaised;
		bool isWithdrawn;
		CampaignStatus status;
	}
	/// @custom:storage-location erc7201:gainz.LaunchPair.storage
	struct MainStorage {
		// Mapping from campaign ID to Campaign struct
		mapping(uint256 => Campaign) campaigns;
		// Mapping from campaign ID to a participant's address to their contribution amount
		mapping(uint256 => mapping(address => uint256)) contributions;
		// Mapping from a user's address to the set of campaign IDs they participated in
		mapping(address => EnumerableSet.UintSet) userCampaigns;
		// Set of all campaign IDs
		EnumerableSet.UintSet activeCampaigns;
		// Total number of campaigns created
		uint256 campaignCount;
		GToken gToken;
	}

	// Event emitted when a new campaign is created
	event CampaignCreated(
		uint256 indexed campaignId,
		address indexed creator,
		uint256 goal,
		uint256 deadline
	);

	// Event emitted when a contribution is made to a campaign
	event ContributionMade(
		uint256 indexed campaignId,
		address indexed contributor,
		uint256 amount
	);

	// Event emitted when tokens are distributed to a participant
	event TokensDistributed(
		uint256 indexed campaignId,
		address indexed contributor,
		uint256 amount
	);

	// Event emitted when the campaign creator withdraws funds after a successful campaign
	event FundsWithdrawn(
		uint256 indexed campaignId,
		address indexed creator,
		uint256 amount
	);

	// Event emitted when a refund is issued to a participant after a failed campaign
	event RefundIssued(
		uint256 indexed campaignId,
		address indexed contributor,
		uint256 amount
	);

	// keccak256(abi.encode(uint256(keccak256("gainz.LaunchPair.storage")) - 1)) & ~bytes32(uint256(0xff));
	bytes32 private constant LAUNCHPAIR_STORAGE_LOCATION =
		0x66c8a6ef269fb788d035dbcef8eb7fb6f4739f9cf4d2b8fcd6329d955e05b300;

	function _getMainStorage() private pure returns (MainStorage storage $) {
		assembly {
			$.slot := LAUNCHPAIR_STORAGE_LOCATION
		}
	}

	// Modifier to ensure the caller is the creator of the campaign
	modifier onlyCreator(uint256 _campaignId) {
		require(
			msg.sender == _getMainStorage().campaigns[_campaignId].creator,
			"Not campaign creator"
		);
		_;
	}

	// Modifier to ensure the campaign exists
	modifier campaignExists(uint256 _campaignId) {
		require(
			_getMainStorage().campaigns[_campaignId].creator != address(0),
			"Campaign does not exist"
		);
		_;
	}

	// Modifier to ensure the campaign has not expired
	modifier isNotExpired(uint256 _campaignId) {
		require(
			block.timestamp <=
				_getMainStorage().campaigns[_campaignId].deadline,
			"Campaign expired"
		);
		_;
	}

	// Modifier to ensure the campaign has met its funding goal
	modifier hasMetGoal(uint256 _campaignId) {
		MainStorage storage $ = _getMainStorage();

		require(
			$.campaigns[_campaignId].fundsRaised >=
				$.campaigns[_campaignId].goal,
			"Goal not met"
		);
		_;
	}

	// Modifier to ensure the campaign funds have not been withdrawn yet
	modifier hasNotWithdrawn(uint256 _campaignId) {
		MainStorage storage $ = _getMainStorage();

		require(
			!$.campaigns[_campaignId].isWithdrawn,
			"Funds already withdrawn"
		);
		_;
	}

	// Modifier to ensure the caller is a participant in the specified campaign
	modifier isCampaignParticipant(address user, uint256 _campaignId) {
		MainStorage storage $ = _getMainStorage();

		require(
			$.userCampaigns[user].contains(_campaignId),
			"Not a participant of selected campaign"
		);
		_;
	}

	function initialize(address _gToken) public initializer {
		MainStorage storage $ = _getMainStorage();

		__Ownable_init(msg.sender);
		$.gToken = GToken(_gToken);
	}

	/**
	 * @dev Creates a new crowdfunding campaign.
	 * @param _creator The address of the campaign creator.
	 * @return campaignId The ID of the newly created campaign.
	 */
	function createCampaign(
		address _creator
	) external onlyOwner returns (uint256 campaignId) {
		MainStorage storage $ = _getMainStorage();

		campaignId = ++$.campaignCount;
		$.campaigns[campaignId] = Campaign({
			creator: payable(_creator),
			goal: 0,
			deadline: 0,
			fundsRaised: 0,
			gtokenNonce: 0,
			isWithdrawn: false,
			status: CampaignStatus.Pending
		});
	}

	function receiveGToken(
		TokenPayment calldata payment,
		uint256 _campaignId
	) external onlyOwner campaignExists(_campaignId) hasMetGoal(_campaignId) {
		MainStorage storage $ = _getMainStorage();

		require(
			payment.amount > 0 &&
				payment.nonce > 0 &&
				address($.gToken) == payment.token,
			"LaunchPair: Invalid GToken received"
		);
		payment.receiveSFT();

		Campaign storage campaign = $.campaigns[_campaignId];
		require(
			campaign.gtokenNonce == 0,
			"Launchpair: Campaign received gToken already"
		);
		campaign.gtokenNonce = payment.nonce;
	}

	/**
	 * @dev Starts a created campaign.
	 * @param _goal The funding goal for the campaign.
	 * @param _duration The duration of the campaign in seconds.
	 * @param _campaignId The ID of the newly created campaign.
	 */
	function startCampaign(
		uint256 _goal,
		uint256 _duration,
		uint256 _campaignId
	) external onlyCreator(_campaignId) {
		require(
			_goal > 0 && _duration > 0 && _duration <= 180 days,
			"Invalid input"
		);
		MainStorage storage $ = _getMainStorage();

		Campaign storage campaign = $.campaigns[_campaignId];
		require(
			campaign.status == CampaignStatus.Pending,
			"Campaign begun already"
		);

		campaign.goal = _goal;
		campaign.deadline = block.timestamp + _duration;
		campaign.status = CampaignStatus.Funding;

		$.activeCampaigns.add(_campaignId);
		emit CampaignCreated(
			_campaignId,
			msg.sender,
			_goal,
			block.timestamp + _duration
		);
	}

	/**
	 * @dev Contribute to a crowdfunding campaign.
	 * @param _campaignId The ID of the campaign to contribute to.
	 */
	function contribute(
		uint256 _campaignId
	) external payable campaignExists(_campaignId) isNotExpired(_campaignId) {
		require(msg.value >= 150e18, "Minimum contribution is 150");
		MainStorage storage $ = _getMainStorage();

		Campaign storage campaign = $.campaigns[_campaignId];
		require(
			campaign.status == CampaignStatus.Funding,
			"Campaign is not in funding status"
		);

		uint256 weiAmount = msg.value;
		campaign.fundsRaised += weiAmount;
		$.contributions[_campaignId][msg.sender] += weiAmount;

		// Add the campaign to the user's participated campaigns if this is their first contribution
		if ($.contributions[_campaignId][msg.sender] == weiAmount) {
			$.userCampaigns[msg.sender].add(_campaignId);
		}

		emit ContributionMade(_campaignId, msg.sender, weiAmount);
	}

	/**
	 * @dev Withdraw funds after the campaign successfully meets its goal.
	 * @param _campaignId The ID of the campaign to withdraw funds from.
	 */
	function withdrawFunds(
		uint256 _campaignId
	)
		external
		campaignExists(_campaignId)
		onlyOwner
		hasMetGoal(_campaignId)
		hasNotWithdrawn(_campaignId)
		returns (uint256 amount)
	{
		MainStorage storage $ = _getMainStorage();

		Campaign storage campaign = $.campaigns[_campaignId];

		amount = campaign.fundsRaised;
		campaign.isWithdrawn = true;
		campaign.status = CampaignStatus.Success;

		// Remove the campaign from the set of all campaigns
		_removeCampaignFromActiveCampaigns(_campaignId);

		payable(owner()).transfer(amount);
		emit FundsWithdrawn(_campaignId, msg.sender, amount);
	}

	/**
	 * @dev Allows a participant to withdraw their share of launchPair tokens
	 *      after a campaign successfully meets its goals.
	 * @param _campaignId The unique identifier of the campaign.
	 * Requirements:
	 * - The campaign must exist.
	 * - The campaign must have achieved its funding goal.
	 * - The sender must be a participant in the specified campaign.
	 */
	function withdrawLaunchPairToken(
		uint256 _campaignId
	)
		external
		campaignExists(_campaignId)
		hasMetGoal(_campaignId)
		isCampaignParticipant(msg.sender, _campaignId)
	{
		MainStorage storage $ = _getMainStorage();
		Campaign storage campaign = $.campaigns[_campaignId];

		require(
			campaign.status == CampaignStatus.Success,
			"Campaign must be successful to withdraw tokens"
		);

		uint256 userLiqShare;

		{
			// Fetch total liquidity from the campaign's gToken nonce.
			GTokenBalance memory gTokenBalance = $.gToken.getBalanceAt(
				address(this),
				campaign.gtokenNonce
			);

			require(
				gTokenBalance.attributes.lpDetails.liquidity > 0,
				"No liquidity available for distribution"
			);

			// Calculate user's liquidity share based on contribution proportion.
			uint256 contribution = $.contributions[_campaignId][msg.sender];
			require(
				contribution > 0,
				"No contributions from sender in this campaign"
			);
			uint256 unUsedContributions = gTokenBalance
				.attributes
				.lpDetails
				.liqValue * 2;
			assert(
				contribution <= unUsedContributions &&
					unUsedContributions <= campaign.fundsRaised
			);

			userLiqShare = FullMath.mulDiv(
				contribution,
				gTokenBalance.attributes.lpDetails.liquidity,
				unUsedContributions
			);

			// Split the liquidity between the contract and the user.
			address[] memory addresses = new address[](2);
			uint256[] memory portions = new uint256[](2);

			addresses[0] = address(this);
			portions[0] =
				gTokenBalance.attributes.lpDetails.liquidity -
				userLiqShare;

			addresses[1] = msg.sender;
			portions[1] = userLiqShare;

			uint256[] memory nonces = $.gToken.split(
				campaign.gtokenNonce,
				addresses,
				portions
			);

			// Update the campaign's gToken nonce with the remaining contract liquidity.
			campaign.gtokenNonce = nonces[0];
		}

		// Remove the campaign from the user's participation list.
		_removeCampaignFromUserCampaigns(msg.sender, _campaignId);

		emit TokensDistributed(_campaignId, msg.sender, userLiqShare);
	}

	/**
	 * @dev Request a refund after a failed campaign.
	 * @param _campaignId The ID of the campaign to refund.
	 */
	function getRefunded(
		uint256 _campaignId
	)
		external
		campaignExists(_campaignId)
		isCampaignParticipant(msg.sender, _campaignId)
	{
		MainStorage storage $ = _getMainStorage();

		Campaign storage campaign = $.campaigns[_campaignId];
		require(
			block.timestamp > campaign.deadline &&
				campaign.fundsRaised < campaign.goal,
			"Refund not available"
		);

		uint256 amount = $.contributions[_campaignId][msg.sender];
		require(amount > 0, "No contributions to refund");

		$.contributions[_campaignId][msg.sender] = 0;
		payable(msg.sender).transfer(amount);

		// Update the status to Failed
		campaign.status = CampaignStatus.Failed;

		// Remove the campaign from the user's participated campaigns after refund
		_removeCampaignFromUserCampaigns(msg.sender, _campaignId);

		emit RefundIssued(_campaignId, msg.sender, amount);
	}

	/**
	 * @dev Get details of a specific campaign.
	 * @param _campaignId The ID of the campaign to get details of.
	 * @return campaign The Campaign struct containing all details of the campaign.
	 */
	function getCampaignDetails(
		uint256 _campaignId
	) external view returns (Campaign memory) {
		MainStorage storage $ = _getMainStorage();

		return $.campaigns[_campaignId];
	}

	/**
	 * @dev Get all campaign IDs.
	 * @return campaignIds An array of all campaign IDs.
	 */
	function getActiveCampaigns() external view returns (uint256[] memory) {
		MainStorage storage $ = _getMainStorage();

		return $.activeCampaigns.values();
	}

	/**
	 * @dev Get campaign IDs that a user has participated in.
	 * @param user The address of the user.
	 * @return campaignIds An array of campaign IDs that the user has participated in.
	 */
	function getUserCampaigns(
		address user
	) external view returns (uint256[] memory) {
		MainStorage storage $ = _getMainStorage();

		return $.userCampaigns[user].values();
	}

	/**
	 * @dev Remove a campaign from the set of all campaigns after it's successful or failed.
	 * @param campaignId The ID of the campaign to remove.
	 */
	function _removeCampaignFromActiveCampaigns(uint256 campaignId) internal {
		MainStorage storage $ = _getMainStorage();

		$.activeCampaigns.remove(campaignId);
	}

	/**
	 * @dev Remove a campaign from the user's participated campaigns after withdrawal or refund.
	 * @param user The address of the user.
	 * @param campaignId The ID of the campaign to remove.
	 */
	function _removeCampaignFromUserCampaigns(
		address user,
		uint256 campaignId
	) internal {
		MainStorage storage $ = _getMainStorage();

		$.userCampaigns[user].remove(campaignId);
	}

	function campaigns(
		uint256 campaignId
	) public view returns (Campaign memory) {
		return _getMainStorage().campaigns[campaignId];
	}

	function contributions(
		uint256 campaignId,
		address contributor
	) public view returns (uint256) {
		return _getMainStorage().contributions[campaignId][contributor];
	}

	function campaignCount() public view returns (uint256) {
		return _getMainStorage().campaignCount;
	}
}
","keccak256":"0xfce38a4e50baa3df9d76b118bcc687b4daabbd2f67f095a4ae442d4f4e12016c","license":"MIT"},"contracts/Pair.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.28;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import { IPair } from "./interfaces/IPair.sol";
import { ISwapFactory } from "./interfaces/ISwapFactory.sol";

import { Math } from "./libraries/Math.sol";
import { UQ112x112 } from "./libraries/UQ112x112.sol";
import { FullMath } from "./libraries/FullMath.sol";

import { PairERC20 } from "./abstracts/PairERC20.sol";

import "./types.sol";

contract Pair is IPair, PairERC20, OwnableUpgradeable {
	using UQ112x112 for uint224;

	uint constant MINIMUM_LIQUIDITY = 10 ** 3;

	uint constant FEE_BASIS_POINTS = 100_00; // 100%
	uint constant MINIMUM_FEE = 5; // 0.05%
	uint constant MAXIMUM_FEE = 500; // 5%

	bytes4 private constant SELECTOR =
		bytes4(keccak256(bytes("transfer(address,uint256)")));

	/// @custom:storage-location erc7201:gainz.Pair.storage
	struct PairStorage {
		address router;
		address token0;
		address token1;
		uint112 reserve0;
		uint112 reserve1;
		uint32 blockTimestampLast;
		uint price0CumulativeLast;
		uint price1CumulativeLast;
		uint kLast; // reserve0 * reserve1, as of immediately after the most recent liquidity event
		uint unlocked;
		uint minFee;
		uint maxFee;
	}
	// keccak256(abi.encode(uint256(keccak256("gainz.Pair.storage")) - 1)) & ~bytes32(uint256(0xff));
	bytes32 private constant PAIR_STORAGE_LOCATION =
		0x052a7ca952fd79e6951e1e37bbd8a7a728c978d413c271dcc4d73117e8490200;

	function _getPairStorage() private pure returns (PairStorage storage $) {
		assembly {
			$.slot := PAIR_STORAGE_LOCATION
		}
	}

	modifier lock() {
		PairStorage storage $ = _getPairStorage();

		require($.unlocked == 1, "Pair: LOCKED");
		$.unlocked = 0;
		_;
		$.unlocked = 1;
	}

	// called once by the router at time of deployment
	function initialize(address _token0, address _token1) external initializer {
		__Ownable_init(msg.sender);
		__PairERC20_init();

		PairStorage storage $ = _getPairStorage();

		$.router = msg.sender;
		$.token0 = _token0;
		$.token1 = _token1;
		$.unlocked = 1;
		$.minFee = MINIMUM_FEE;
		$.maxFee = MAXIMUM_FEE;
	}

	// update reserves and, on the first call per block, price accumulators
	function _update(
		uint balance0,
		uint balance1,
		uint112 reserve0,
		uint112 reserve1
	) private {
		PairStorage storage $ = _getPairStorage();

		require(
			balance0 <= type(uint112).max && balance1 <= type(uint112).max,
			"Pair: OVERFLOW"
		);

		uint32 blockTimestamp = uint32(block.timestamp % 2 ** 32);
		uint32 timeElapsed = blockTimestamp - $.blockTimestampLast; // Overflow is intentional here

		if (timeElapsed > 0 && reserve0 != 0 && reserve1 != 0) {
			// * never overflows, and + overflow is desired
			$.price0CumulativeLast +=
				uint(UQ112x112.encode(reserve1).uqdiv(reserve0)) *
				timeElapsed;
			$.price1CumulativeLast +=
				uint(UQ112x112.encode(reserve0).uqdiv(reserve1)) *
				timeElapsed;
		}

		$.reserve0 = uint112(balance0);
		$.reserve1 = uint112(balance1);
		$.blockTimestampLast = blockTimestamp;
		emit Sync($.reserve0, $.reserve1);
	}

	function router() external view returns (address) {
		return _getPairStorage().router;
	}

	function token0() external view returns (address) {
		return _getPairStorage().token0;
	}

	function token1() external view returns (address) {
		return _getPairStorage().token1;
	}

	function getReserves()
		public
		view
		returns (uint112 reserve0, uint112 reserve1, uint32 _blockTimestampLast)
	{
		PairStorage storage $ = _getPairStorage();

		reserve0 = $.reserve0;
		reserve1 = $.reserve1;
		_blockTimestampLast = $.blockTimestampLast;
	}

	function _safeTransfer(address token, address to, uint value) private {
		(bool success, bytes memory data) = token.call(
			abi.encodeWithSelector(SELECTOR, to, value)
		);
		require(
			success && (data.length == 0 || abi.decode(data, (bool))),
			"Pair: TRANSFER_FAILED"
		);
	}

	function price0CumulativeLast() external view returns (uint256) {
		return _getPairStorage().price0CumulativeLast;
	}

	function price1CumulativeLast() external view returns (uint256) {
		return _getPairStorage().price1CumulativeLast;
	}

	// this low-level function should be called from a contract which performs important safety checks
	function mint(address to) external lock onlyOwner returns (uint liquidity) {
		PairStorage storage $ = _getPairStorage();

		(uint112 reserve0, uint112 reserve1, ) = getReserves(); // gas savings
		uint balance0 = IERC20($.token0).balanceOf(address(this));
		uint balance1 = IERC20($.token1).balanceOf(address(this));
		uint amount0 = balance0 - reserve0;
		uint amount1 = balance1 - reserve1;

		uint _totalSupply = totalSupply();
		if (_totalSupply == 0) {
			liquidity = Math.sqrt(amount0 * amount1) - MINIMUM_LIQUIDITY;
			_mint(address(0), MINIMUM_LIQUIDITY); // permanently lock the first MINIMUM_LIQUIDITY tokens
		} else {
			liquidity = Math.min(
				(amount0 * _totalSupply) / reserve0,
				(amount1 * _totalSupply) / reserve1
			);
		}
		require(liquidity > 0, "Pair: INSUFFICIENT_LIQUIDITY_MINTED");
		_mint(to, liquidity);

		_update(balance0, balance1, reserve0, reserve1);
		emit Mint(msg.sender, amount0, amount1);
	}

	// this low-level function should be called from a contract which performs important safety checks
	function burn(
		address to
	) external lock onlyOwner returns (uint amount0, uint amount1) {
		PairStorage storage $ = _getPairStorage();

		(uint112 _reserve0, uint112 _reserve1) = ($.reserve0, $.reserve1); // gas savings
		address _token0 = $.token0; // gas savings
		address _token1 = $.token1; // gas savings
		uint balance0 = IERC20(_token0).balanceOf(address(this));
		uint balance1 = IERC20(_token1).balanceOf(address(this));
		uint liquidity = balanceOf(address(this));

		uint _totalSupply = totalSupply();
		amount0 = (liquidity * balance0) / _totalSupply; // using balances ensures pro-rata distribution
		amount1 = (liquidity * balance1) / _totalSupply; // using balances ensures pro-rata distribution
		require(
			amount0 > 0 && amount1 > 0,
			"PairV2: INSUFFICIENT_LIQUIDITY_BURNED"
		);
		_burn(address(this), liquidity);
		_safeTransfer(_token0, to, amount0);
		_safeTransfer(_token1, to, amount1);
		balance0 = IERC20(_token0).balanceOf(address(this));
		balance1 = IERC20(_token1).balanceOf(address(this));

		_update(balance0, balance1, _reserve0, _reserve1);
		emit Burn(msg.sender, amount0, amount1, to);
	}

	// if fee is on, mint liquidity equivalent to feePercent charged from the swap
	function _mintSwapFee(
		uint112 _reserve0,
		uint112 _reserve1,
		uint feePercent
	) private returns (bool feeOn) {
		PairStorage storage $ = _getPairStorage();

		address feeTo = ISwapFactory($.router).feeTo();
		feeOn = feeTo != address(0);
		uint _kLast = $.kLast; // gas savings
		if (feeOn) {
			// We don't restrict to only when _kLast > 0 since all swaps generate fees
			uint rootK = Math.sqrt(uint(_reserve0) * (_reserve1));
			uint rootKLast = Math.sqrt(_kLast);
			if (rootK > rootKLast) {
				uint numerator = totalSupply() * (rootK - rootKLast);
				uint denominator = (rootK * feePercent) + (rootKLast);
				uint liquidity = numerator / denominator;
				if (liquidity > 0) _mint(feeTo, liquidity);
			}
		} else if (_kLast != 0) {
			$.kLast = 0;
		}
	}

	struct SwapVariables {
		uint balance0;
		uint balance1;
		uint112 reserve0;
		uint112 reserve1;
	}

	function swap(
		uint amount0Out,
		uint feePercent0,
		uint amount1Out,
		uint feePercent1,
		address to
	) external lock onlyOwner {
		require(
			amount0Out > 0 || amount1Out > 0,
			"GainzSwap: INSUFFICIENT_OUTPUT_AMOUNT"
		);

		SwapVariables memory swapVars;

		(swapVars.reserve0, swapVars.reserve1, ) = getReserves(); // gas savings
		require(
			amount0Out < swapVars.reserve0 && amount1Out < swapVars.reserve1,
			"GainzSwap: INSUFFICIENT_LIQUIDITY"
		);

		bool feeOn = _mintSwapFee(
			swapVars.reserve0,
			swapVars.reserve1,
			feePercent0 + feePercent1
		);

		PairStorage storage $ = _getPairStorage();

		{
			// scope for $.token{0,1}, avoids stack too deep errors
			require(to != $.token0 && to != $.token1, "GainzSwap: INVALID_TO");

			if (amount0Out > 0) _safeTransfer($.token0, to, amount0Out); // optimistically transfer tokens
			if (amount1Out > 0) _safeTransfer($.token1, to, amount1Out); // optimistically transfer tokens

			swapVars.balance0 = IERC20($.token0).balanceOf(address(this));
			swapVars.balance1 = IERC20($.token1).balanceOf(address(this));
		}

		uint amount0In = swapVars.balance0 > swapVars.reserve0 - amount0Out
			? swapVars.balance0 - (swapVars.reserve0 - amount0Out)
			: 0;
		uint amount1In = swapVars.balance1 > swapVars.reserve1 - amount1Out
			? swapVars.balance1 - (swapVars.reserve1 - amount1Out)
			: 0;
		require(
			amount0In > 0 || amount1In > 0,
			"GainzSwap: INSUFFICIENT_INPUT_AMOUNT"
		);

		{
			uint balance0Adjusted = (swapVars.balance0 * FEE_BASIS_POINTS) -
				(amount0In * feePercent0);

			uint balance1Adjusted = (swapVars.balance1 * FEE_BASIS_POINTS) -
				(amount1In * feePercent1);

			require(
				balance0Adjusted * balance1Adjusted >=
					uint(swapVars.reserve0) *
						uint(swapVars.reserve1) *
						FEE_BASIS_POINTS ** 2,
				"GainzSwap: K"
			);
		}

		_update(
			swapVars.balance0,
			swapVars.balance1,
			swapVars.reserve0,
			swapVars.reserve1
		);
		if (feeOn) $.kLast = uint($.reserve0) * ($.reserve1); // reserve0 and reserve1 are up-to-date

		emit Swap(msg.sender, amount0In, amount1In, amount0Out, amount1Out, to);
	}

	function sync() external {
		PairStorage storage $ = _getPairStorage();

		_update(
			IERC20($.token0).balanceOf(address(this)),
			IERC20($.token1).balanceOf(address(this)),
			$.reserve0,
			$.reserve1
		);
	}

	function calculateFeePercent(
		uint256 amount,
		uint256 reserve
	) public view returns (uint256) {
		(uint256 reserve0, uint256 reserve1, ) = getReserves();

		require(
			reserve == reserve0 || reserve == reserve1,
			"GainzSwap: INVALID_RESERVE"
		);

		(uint256 minFeePercent, uint256 maxFeePercent) = feePercents();

		// Calculate the reserve gap
		uint256 reserveGap = (reserve == reserve0 && reserve0 > reserve1)
			? reserve0 - reserve1
			: (reserve == reserve1 && reserve1 > reserve0)
				? reserve1 - reserve0
				: 0;

		uint256 totalLiquidity = totalSupply();
		// - balanceOf(ISwapFactory(_getPairStorage().router).feeTo());

		uint256 liquidity = ((amount + reserveGap) * totalLiquidity) / reserve;

		// Calculate feePercent and bound it to minFeePercent and maxFeePercent
		uint256 feePercent = minFeePercent +
			(liquidity * (maxFeePercent - minFeePercent)) /
			totalLiquidity;

		return feePercent > maxFeePercent ? maxFeePercent : feePercent;
	}

	function feePercents() public view returns (uint256, uint256) {
		PairStorage storage $ = _getPairStorage();

		return (
			$.minFee == 0 ? MINIMUM_FEE : $.minFee,
			$.maxFee == 0 ? MAXIMUM_FEE : $.maxFee
		);
	}
}
","keccak256":"0x59c60b6291f67c71e7c34557ddbf2f1efcc0b15983f28d4b64c6f886a84685a5","license":"GPL-3.0-or-later"},"contracts/PriceOracle.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.28;

import { FixedPoint } from "./libraries/FixedPoint.sol";
import { AMMLibrary } from "./libraries/AMMLibrary.sol";
import { OracleLibrary } from "./libraries/OracleLibrary.sol";

import { IPair } from "./interfaces/IPair.sol";

import { IRouter } from "./interfaces/IRouter.sol";

import "./types.sol";

// fixed window oracle that recomputes the average price for the entire period once every period
// note that the price average is only guaranteed to be over at least 1 period, but may be over a longer period
contract PriceOracle {
	using FixedPoint for *;

	uint public constant PERIOD = 60 minutes;

	address public immutable router;
	address public immutable pairsBeacon;

	mapping(address => address) public token0;
	mapping(address => address) public token1;

	mapping(address => uint) public price0CumulativeLast;
	mapping(address => uint) public price1CumulativeLast;
	mapping(address => uint32) public blockTimestampLast;
	mapping(address => FixedPoint.uq112x112) public price0Average;
	mapping(address => FixedPoint.uq112x112) public price1Average;

	constructor() {
		router = msg.sender;
		pairsBeacon = IRouter(router).getPairsBeacon();
	}

	function add(address tokenA, address tokenB) external {
		address pair = pairFor(tokenA, tokenB);
		IPair _pair = IPair(pair);

		token0[pair] = _pair.token0();
		token1[pair] = _pair.token1();
		price0CumulativeLast[pair] = _pair.price0CumulativeLast(); // fetch the current accumulated price value (1 / 0)
		price1CumulativeLast[pair] = _pair.price1CumulativeLast(); // fetch the current accumulated price value (0 / 1)
		uint112 reserve0;
		uint112 reserve1;
		(reserve0, reserve1, blockTimestampLast[pair]) = _pair.getReserves();
		require(reserve0 != 0 && reserve1 != 0, "PriceOracle: NO_RESERVES"); // ensure that there's liquidity in the pair
	}

	function pairFor(
		address tokenA,
		address tokenB
	) public view returns (address) {
		return AMMLibrary.pairFor(router, pairsBeacon, tokenA, tokenB);
	}

	function update(address pair) public {
		(
			uint price0Cumulative,
			uint price1Cumulative,
			uint32 blockTimestamp
		) = OracleLibrary.currentCumulativePrices(pair);
		uint32 timeElapsed = blockTimestamp - blockTimestampLast[pair]; // overflow is desired

		// silently fail if period not elapsed
		if (
			price0Average[pair].mul(1).decode144() != 0 &&
			price1Average[pair].mul(1).decode144() != 0 &&
			timeElapsed < PERIOD
		) return;

		// overflow is desired, casting never truncates
		// cumulative price is in (uq112x112 price * seconds) units so we simply wrap it after division by time elapsed
		price0Average[pair] = FixedPoint.uq112x112(
			uint224(
				(price0Cumulative - price0CumulativeLast[pair]) / timeElapsed
			)
		);
		price1Average[pair] = FixedPoint.uq112x112(
			uint224(
				(price1Cumulative - price1CumulativeLast[pair]) / timeElapsed
			)
		);

		price0CumulativeLast[pair] = price0Cumulative;
		price1CumulativeLast[pair] = price1Cumulative;
		blockTimestampLast[pair] = blockTimestamp;
	}

	// note this will always return 0 before update has been called successfully for the first time.
	function _consult(
		address pair,
		address token,
		uint amountIn
	) internal view returns (uint amountOut) {
		if (token == token0[pair]) {
			amountOut = price0Average[pair].mul(amountIn).decode144();
		} else {
			require(token == token1[pair], "PriceOracle: INVALID_TOKEN");
			amountOut = price1Average[pair].mul(amountIn).decode144();
		}
	}

	function consult(
		address tokenIn,
		address tokenOut,
		uint amountIn
	) external view returns (uint) {
		address pair = AMMLibrary.pairFor(
			router,
			pairsBeacon,
			tokenIn,
			tokenOut
		);

		return _consult(pair, tokenIn, amountIn);
	}

	function updateAndConsult(
		address tokenIn,
		address tokenOut,
		uint amountIn
	) external returns (uint) {
		address pair = AMMLibrary.pairFor(
			router,
			pairsBeacon,
			tokenIn,
			tokenOut
		);
		update(pair);

		return _consult(pair, tokenIn, amountIn);
	}
}
